import { reducer } from "./Product/reducer";

import { createStore } from "redux";

const store = createStore(reducer);

export { store }; export const addCart = (product) => {

    return {
        type: "ADDITEM",
        payload: product
    }

}
export const delCart = (product) => {
    return {
        type: "DELITEM",
        payload: product
    }
}
