import {
    STORE_DATA
    // CAT_DATA
} from './actionTypes';

const storeData = (payload) => ({
    type: STORE_DATA,
    payload: payload
})

const addCart = (product) => {
    return {
        type: "ADDITEM",
        payload: product
    }
}

const delCart = (product) => {
    return {
        type: "DELITEM",
        payload: product
    }
}


// const catData = (payload) => ({
//     type: CAT_DATA,
//     payload: payload
// })

export { storeData, addCart, delCart }
