import {
    STORE_DATA,
    SEARCH_DATA
} from "./actionTypes";


const initState = {
    data: [],
    search: []
}

// const newState = { ...state, search: state.data && state.data.filter((el) => el.title === action.payload.title) }


const reducer = (state = initState, action) => {
    switch (action.type) {
        case STORE_DATA:
            return { ...state, data: action.payload }

        case SEARCH_DATA:
            const newState = { ...state, search: state.data && state.data.filter((el) => el.type.toLowerCase().includes(action.payload.title.toLowerCase())) };
            console.log(newState);
            return newState;
        default:
            return state;
    }
}

export { reducer }
