import { reducer } from "./Product/reducer";

import { handleCart } from "./Cart/reducer"

import { createStore, combineReducers } from "redux";


const mainReducer = combineReducers({
    store: reducer,
    cart: handleCart
})

const store = createStore(mainReducer);


export { store };
