import React from 'react'
import { useSelector } from 'react-redux';
import "./cart.css"

export const Cart = () => {



    const cartdata = useSelector((state) => state.handleCart);

    // console.log(cartdata);
    const increment = (e) => {
        console.log(e + 1)
    }

    // delCart

    return (
        <div>
            <h1>Cart</h1>

            {
                // { cart.qty } X Rs.{cart.price} = {cart.qty * cart.price}
                cartdata.map((e) => {
                    return (
                        <div>
                            <div className="cart_cont">
                                <img src={e.img} alt={e.title} />
                                <h3>{e.title}</h3>
                                <button onClick={() => increment(e.qty)}>+</button>
                                <p>{e.qty}</p>
                                <button>-</button>
                                <p>Rs.{e.price}</p>
                                <p>total Price {e.qty * Number(e.price)}</p>
                                <button>delete</button>
                            </div>
                            <hr></hr>
                        </div>
                    )
                })
            }

        </div>
    )
}
