import { STORE_DATA, CAT_DATA } from "./actionTypes";


const initState = {
    data: []
}


const reducer = (state = initState, action) => {
    switch (action.type) {
        case STORE_DATA:
            return { ...state, data: action.payload }

        case CAT_DATA:
            return { ...state, data: state.data.filter((el) => { if (el.cat == action.payload.cat) return el }) }

        default:
            return state;
    }
}

export { reducer }
