import React, { useState, useEffect } from 'react';
import { useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import {
    Box,
    Card,
    CardContent,
    CardActions,
    Typography,
    Button,
    CardMedia
} from '@mui/material';
import "./product.css"
import Pagination from '@mui/material/Pagination';
import Stack from '@mui/material/Stack';
// import Button from '@mui/material/Button';

import { useNavigate } from "react-router-dom";

export const Product = () => {

    const [sorting, setSorting] = useState("");
    const [database, setDatabase] = useState([]);


    const { cat } = useParams();

    const navigate = useNavigate()
    // console.log(cat)

    const res = useSelector((state) => state.data);
    // console.log(res)

    // console.log(sorting)
    useEffect(() => {

        var result = res.filter((categ) => categ.category === cat)
            .sort((a, b) => {
                if (sorting === "asc") {
                    return a.costforone - b.costforone
                }
                else if (sorting === "desc") {
                    return b.costforone - a.costforone
                } else {
                    return 0
                }
            })
        // console.log(result);
        setDatabase(result);
    }, [sorting]);


    return (
        <div className="product-cont">
            <h4>sort by cost</h4>
            <Stack spacing={2} direction="row">
                <Button variant="outlined" onClick={() => setSorting("asc")}>Low To High</Button>
                <Button variant="outlined" onClick={() => setSorting("desc")}>High To Low</Button>
            </Stack>
            {/* <button>low to high</button>
            <button >high to low</button> */}
            <h1>Show Products</h1>
            <div className="product-cont1">
                {
                    // res.filter((categ) => {
                    //     return categ.category === cat
                    // })
                    database.map((data, index) => {
                        return (
                            <>
                                <Box width='300px' key={index}>
                                    <Card>
                                        <CardMedia
                                            component='img'
                                            height='200'
                                            image={data.img}
                                            alt='unsplash image'
                                        />
                                        <CardContent>
                                            <Typography gutterBottom variant='h5' component='div'>
                                                {data.title}
                                            </Typography>
                                            <Typography gutterBottom variant='p' component='div'>
                                                {data.price}
                                            </Typography>
                                        </CardContent>
                                        <CardActions>
                                            <Button size='small' onClick={() => { navigate(`/productDetails/${data.id}`) }}>Buy Now</Button>
                                        </CardActions>
                                    </Card>
                                </Box>
                            </>
                        )
                    })
                }
            </div>
            <Stack spacing={2}>
                <Pagination count={5} variant="outlined" shape="rounded" />
            </Stack>
        </div>
    )
}
